import mopdel.Customer;

import javax.servlet.*;
import javax.servlet.http.*;
import javax.servlet.annotation.*;
import java.io.IOException;
import java.util.ArrayList;
import java.util.List;

@WebServlet(name = "DisplayServlet", value = "/display")
public class DisplayServlet extends HttpServlet {
    @Override
    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
        request.setCharacterEncoding("UTF-8");

    }

    @Override
    protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
        request.setCharacterEncoding("UTF-8");

        List<Customer> customers = new ArrayList<>();
        customers.add(new Customer("Hà Bảo Ân", "1993", "Daklak", "data:image/jpeg;base64,"));
        customers.add(new Customer("Huỳnh Trung Thuyên", "1994", "Huế", "data:image/jpeg;base64,"));
        customers.add(new Customer("Hoàng Minh trí", "1998", "Huế", "data:image/jpeg;base64,"));
        customers.add(new Customer("Bùi Hùng", "1993", "Đà Nẵng", "data:image/jpeg;base64,"));
        customers.add(new Customer("Đặng Ngọc Huy", "1998", "Quảng Nam", "data:image/jpeg;base64,"));
        RequestDispatcher requestDispatcher = request.getRequestDispatcher("result.jsp");
        request.setAttribute("customer", customers);
        requestDispatcher.forward(request, response);


    }
}
